.swiper-slide {
    position: relative;
    display: flex;
    justify-content: center;
    background: #fff;
}

.radius {
    border: 1px solid #ddd;
    border-radius: 10px;
    box-sizing: border-box;
}

.productlink {
    display: block;
    width: 100%;
    height: 100%;
    color: #000;
    text-decoration: none;
    text-align: center;

    img {
        margin-top: 10px;

        &:hover {
            transform: scale(1.15);
        }
    }
}

.description {
    width: 100%;
    position: absolute;
    bottom: 0;
}

.more {
    position: absolute;
    margin: auto 0;
    right: 30px;
    font-size: 12px;
    font-weight: bold;
}

.newproduct_image {
    height: 120px;
}

.product_image {
    height: 100px;
}

.prev_btn,
.next_btn {
    width: 34px;
    display: flex;
    justify-content: center;
    align-items: center;
}

// promotion
.promotion {
    width: 100%;
    height: 764px;
    margin: 0 auto;
    position: relative;
    overflow: hidden;

    .swiper {
        width: calc(960px * 3 + 60px);
        height: 700px;
        position: absolute;
        top: 40px;
        left: 50%;
        margin-left: calc((960px * 3 + 60px) / -2);

        .swiper-slide {
            opacity: .5;
            transition: opacity 2s;

            img {
                height: 640px;
            }
        }

        .swiper-slide-active {
            opacity: 1;
        }
    }

    .swiper-pagination {
        bottom: 40px;
        left: 0;
        right: 0;

        .swiper-pagination-bullet {
            background-color: transparent;
            background-image: url("../img/promotion_slide_pager1.png");
            width: 10px;
            height: 10px;
            margin-right: 6px;
            outline: none;

            &:last-child {
                margin-right: 0;
            }
        }

        .swiper-pagination-bullet-active {
            background-image: url("../img/promotion_slide_pager_on.png");
        }
    }

    .swiper-prev,
    .swiper-next {
        width: 42px;
        height: 42px;
        position: absolute;
        margin-top: 328px;
        
        z-index: 1;
        cursor: pointer;
        outline: none;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .swiper-prev {
        left: 50%;
        margin-left: -540px;
    }

    .swiper-next {
        right: 50%;
        margin-right: -540px;
    }
}

// new product
.newproduct {
    width: 1240px;
    height: 300px;
    margin: 0 auto;
    margin-top: 60px;

    .newproduct_title {
        position: relative;
        width: 100%;
        height: 80px;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 20px;
        font-weight: bold;
    }

    .newproduct_wrapper {
        display: flex;

        .swiper-prev,
        .swiper-next {
            width: 44px;
            height: 44px;
            position: absolute;
            z-index: 1;
            cursor: pointer;
            outline: none;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .swiper {
            width: calc(218.4px * 5 + 50px);
            height: 200px;
            position: relative;
        }
    }
}

